INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3324, 2584, 11, 100, NULL, NULL, '<img src="/new_platform/static/canvas/images/remember.png" style="float:right;" />The purpose of this unit is to give students dedicated time to research colleges and complete their college list. This unit is a flexible unit, which means you can schedule as many lessons as you need to support your program goals.<p><p>In each class, students should:<ol><li>Research colleges through the websites listed</li><li>Update the College Match and Fit Checklist so they can compare and contrast their options</li><li>Update the College List Tool as relevant</li><li>Update mentors through Conversations</li></ol></p></p>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3325, 2585, 11, 100, NULL, NULL, 'The following Canvas cards are what your class will see during this lesson.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3326, 2586, 11, 100, NULL, NULL, 'Now it’s time to research colleges to develop a college list. You’ll be putting the research you do today in your <a href="https://docs.google.com/spreadsheets/d/1MjU-bWzE9YLSGqL1Q86KVfdXhPbR8o-EuBbgb-0T0Bc/edit?usp=sharing" target="_blank">College Match and Fit Checklist</a>.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3327, 2587, 11, 100, NULL, NULL, 'First, use the following websites to find colleges that align to your preferences.<ul><li><a href="https://bigfuture.collegeboard.org/college-search" target="_blank">BigFuture</a></li><li><a href="https://collegescorecard.ed.gov/" target="_blank">College Scorecard</a></li><li><a href="http://www.communitycollegereview.com/compare-schools" target="_blank">Community College Review</a></li><li><a href="https://www.naviance.com/" target="_blank">Naviance</a></li></ul>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3328, 2588, 11, 100, NULL, NULL, 'As you find colleges that you want to apply to, add them to the <a href="/cchklist/list/" target="_blank">College List Tool</a> so you and your mentor can work on the college application process together.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3329, 2589, 11, 100, NULL, NULL, 'Finally, update your mentor in Conversations on your progress.<ul><li>Tell your mentor about one college you researched. Is it a good fit for you? Why or why not?</li><li>Give your mentor a personal update about your week.</li></ul>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3330, 2590, 11, 100, NULL, NULL, 'Now it’s time to research colleges to develop a college list. You’ll be putting the research you do today in your <a href="https://docs.google.com/spreadsheets/d/1MjU-bWzE9YLSGqL1Q86KVfdXhPbR8o-EuBbgb-0T0Bc/edit?usp=sharing" target="_blank">College Match and Fit Checklist</a>.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3331, 2591, 11, 100, NULL, NULL, 'First, use the following websites to find colleges that align to your preferences.<ul><li><a href="https://bigfuture.collegeboard.org/college-search" target="_blank">BigFuture</a></li><li><a href="https://collegescorecard.ed.gov/" target="_blank">College Scorecard</a></li><li><a href="http://www.communitycollegereview.com/compare-schools" target="_blank">Community College Review</a></li><li><a href="https://www.naviance.com/" target="_blank">Naviance</a></li></ul>', NULL);
