INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3334, 2594, 11, 100, NULL, NULL, 'This unit is all about researching schools to generate a college list customized to your mentee’s preferences.<p><p>For info about why this unit is so important, click <a href="http://learn.imentor.org/help/article/link/the-importance-of-a-college-list" target="_blank">here</a>.</p></p>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3335, 2595, 11, 100, NULL, NULL, 'Today, your mentee used the following websites to research colleges:<ul><li><a href="https://bigfuture.collegeboard.org/college-search" target="_blank">BigFuture</a></li><li><a href="https://collegescorecard.ed.gov/" target="_blank">College Scorecard</a></li><li><a href="http://www.communitycollegereview.com/compare-schools" target="_blank">Community College Review</a></li><li><a href="https://www.naviance.com/" target="_blank">Naviance</a></li></ul>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3336, 2596, 11, 100, NULL, NULL, 'They documented their research in the <a href="https://docs.google.com/spreadsheets/d/1MjU-bWzE9YLSGqL1Q86KVfdXhPbR8o-EuBbgb-0T0Bc/edit?usp=sharing" target="_blank">College Match and Fit Checklist</a>.<p><p>Click <a href="http://learn.imentor.org/help/article/link/reviewing-your-mentees-college-list" target="_blank">here</a> for guidance around what makes a balanced college list and strategies for how you can support them along the way.</p></p>Review their research. What trends do you see? Is your mentee finding a diversity of schools that match their preferences? Are they ignoring schools that you think they should consider?<p><p>Share your thoughts with them in <a href="http://learn.imentor.org/help/article/link/welcome-to-conversations" target="_blank">Conversations</a>.</p></p>', NULL);
