INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3390, 2650, 11, 100, NULL, NULL, 'The following Canvas cards are what your class will see during this lesson.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3391, 2651, 11, 100, NULL, NULL, 'Now it’s time to research colleges to develop a college list. You’ll be putting the research you do today in your <a href="https://docs.google.com/spreadsheets/d/1MjU-bWzE9YLSGqL1Q86KVfdXhPbR8o-EuBbgb-0T0Bc/edit?usp=sharing" target="_blank">College Match and Fit Checklist</a>.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3392, 2652, 11, 100, NULL, NULL, 'First, use the following websites to find colleges that align to your preferences.<ul><li><a href="https://bigfuture.collegeboard.org/college-search" target="_blank">BigFuture</a></li><li><a href="https://collegescorecard.ed.gov/" target="_blank">College Scorecard</a></li><li><a href="http://www.communitycollegereview.com/compare-schools" target="_blank">Community College Review</a></li><li><a href="https://www.naviance.com/" target="_blank">Naviance</a></li></ul>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3393, 2653, 11, 100, NULL, NULL, 'As you find colleges that you want to apply to, add them to the <a href="/cchklist/list/" target="_blank">College List Tool</a> so you and your mentor can work on the college application process together.', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (3394, 2654, 11, 100, NULL, NULL, 'Finally, update your mentor in Conversations on your progress.<ul><li>Tell your mentor about one college you researched. Is it a good fit for you? Why or why not?</li><li>Give your mentor a personal update about your week.</li></ul>', NULL);
