INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (1, 1, 11, 100, NULL, NULL, '<img src="/new_platform/static/canvas/images/read_old.png" style="float:right;" />This lesson introduces new mentees to the iMentor program. It exposes them to program goals, expectations, and common vocabulary they will experience throughout the program: PC, Mentor, Mentee, Relationship, Goals.<p><p>At the end of this lesson, mentees will be able to:<ul><li>Explain a summary of the iMentor program</li><li>Set specific goals for this year</li><li>Describe the role of the PC</li><li>Describe the role of the Mentor</li><li>Describe the role of the Mentee</li><li>Illustrate the different relationships that exist between the PC, the Mentee, and the Mentor</li></ul></p></p>For the complete lesson, click <a href="https://learn.imentor.org/help/article/link/1111---building-relationships---welcome-to-imentor" target="_blank">here</a>.', NULL);
