INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (89, 58, 11, 100, NULL, NULL, '<img src="/new_platform/static/canvas/images/remember.png" style="float:right;" />This lesson is the entry point for the mentee/mentor relationship. This lesson helps prepare both mentees and mentors to communicate appropriate personal information about themselves as they enter into their relationship.<p><p>At the end of this lesson, mentees will be able to:<ul><li>Analyze a poem and examine its meaning</li><li>Select ideas and terms that effectively communicate their own personal context</li><li>Create an "I AM FROM" that identifies and describes where they came from</li><li>Create an "I AM" that identifies and describes key qualities that they currently have</li></ul></p></p><b>For the complete lesson, click <a href="https://learn.imentor.org/help/article/link/np-building-relationships-about-me" target="_blank">here</a>.</b>', NULL);
