INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (94, 61, 11, 200, NULL, NULL, 'Consider the following:<ul><li>What do you think this poem means?</li><li>What does the rose represent?</li><li>What does the concrete represent?</li><li>Does this image/poem resonate? Why or why not?</li></ul>', NULL);
INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (95, 62, 11, 100, NULL, NULL, 'Consider each of the following to create your poem.<ul><li>words that describe your home, family, community</li><li>things you love about your country, neighborhood, community, family, self</li><li>people that have had a strong influence in your life</li><li>powerful experiences you had at an early age</li><li>things you learned the hard way</li></ul><p><p>Now write your poem. Start each line with “I am from…”</p></p>', NULL);
