INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (101, 66, 11, 100, NULL, NULL, '<img src="/new_platform/static/canvas/images/remember.png" style="float:right;" />This lesson introduces new mentees to the iMentor program. It familiarizes them with program goals, expectations, and common vocabulary they will use throughout the program.<p><p>At the end of this lesson, mentees will be able to:<ul><li>Define what a mentor is</li><li>Articulate the goals and purpose of the program</li><li>Describe the role of program participants:<ul style="list-style-type: circle"><li>mentee</li><li>mentor</li><li>staff</li></ul></li><li>Illustrate the purpose of the program and different relationships that exist between program staff, mentee, and mentor</li></ul></p></p><b>For the complete lesson, click <a href="https://learn.imentor.org/help/article/link/building-relationships-introduction-to-imentor-9" target="_blank">here</a>.</b>', NULL);
