INSERT into canvas_widget_content (id, widget_id, content_type, ordinal, filename, url, html, plain_text) VALUES (128, 83, 11, 100, NULL, NULL, '<img src="/new_platform/static/canvas/images/remember.png" style="float:right;" />This lesson re-orients mentees to the goals of the program, gives them an opportunity to welcome each other back to school, and reconnects them with their mentors after summer break.<p><p>At the end of this lesson, mentees will be able to:<ul><li>State the program goals and expectations</li><li>​Reflect on their summer experiences</li><li>Process and revise those experiences through peer feedback</li><li>​Share what they did this summer with their mentor</li><li>Write a reflection to their mentor about what they are most looking forward to this year</li></ul></p></p><b>For the complete lesson, click <a href="https://learn.imentor.org/help/article/link/rp-building-relationships-refresh-and-reconnect-11" target="_blank">here</a>.</b>', NULL);
